import React from "react";
import { VStack, Box, Heading, Text, HStack } from "@chakra-ui/react";
import { DeleteIcon } from "@chakra-ui/icons";

function BookList({ books, onDeleteBook }) {
  return (
    <VStack spacing={4} align="stretch">
      <Heading>My Books</Heading>
      {books.map((book) => (
        <Box key={book.id} p={4} bg="white" shadow="sm" borderRadius="md">
          <HStack justify="space-between">
            <Box>
              <Heading size="md">{book.title}</Heading>
              <Text color="gray.600">{book.author}</Text>
            </Box>
            <Button
              colorScheme="red"
              variant="ghost"
              onClick={() => onDeleteBook(book.id)}
              leftIcon={<DeleteIcon />}
            >
              Delete
            </Button>
          </HStack>
        </Box>
      ))}
    </VStack>
  );
}

export default BookList;
